(function () {
  var $content, FontSize, changeFontSize, incr_px, ls_font_size;

  $(".contact").on("click", function (e) {
    return $(this).toggleClass("show");
  });

  $content = $(".content");

  incr_px = 1;

  FontSize = {
    max: 25,
    min: 10
  };

  ls_font_size = localStorage.getItem("font-size");

  $content.css("font-size", ls_font_size + "px");

  changeFontSize = function (e) {
    var newSize, size, sizeType;
    sizeType = $(e.target).data("size");
    size = parseInt($content.css("font-size"));
    if (sizeType === "larger") {
      newSize = size + incr_px;
      if (newSize < FontSize.max) {
        localStorage.setItem("font-size", newSize);
        $content.css("font-size", newSize + "px");
      }
    }
    if (sizeType === "smaller") {
      newSize = size - incr_px;
      if (newSize > FontSize.min) {
        localStorage.setItem("font-size", newSize);
        return $content.css("font-size", newSize + "px");
      }
    }
  };

  $(".change-fontSize").on("mousedown", function (e) {
    return changeFontSize(e);
  });

}).call(this);

